import gmpy2
from Crypto.Util.number import *


def Commodulus(e1, e2, n, c1, c2):
    g, s, t = gmpy2.gcdext(e1, e2)
    m = gmpy2.powmod(c1, s, n) * gmpy2.powmod(c2, t, n) % n
    print(long_to_bytes(m))

